static void
loader_set_quoted_string_buffer (void)
{
  if (qstr_Buf_pool_idx < QUOTED_STR_BUF_POOL_SIZE)
    {
      qstr_Buf_p = &(qstr_Buf_pool[qstr_Buf_pool_idx][0]);
      qstr_Buf_pool_idx++;
      use_Qstr_malloc_buffer = false;
    }
  else
    {
      if (qstr_Malloc_buffer == NULL)
	{
	  qstr_Malloc_buffer_size = MAX_QUOTED_STR_BUF_SIZE;
	  qstr_Malloc_buffer = malloc (qstr_Malloc_buffer_size);

	  if (qstr_Malloc_buffer == NULL)
	    {
	      er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_OUT_OF_VIRTUAL_MEMORY, 1, (size_t) qstr_Malloc_buffer_size);
	      YY_FATAL_ERROR (er_msg ());
	      return;
	    }
	}

      qstr_Buf_p = qstr_Malloc_buffer;
      use_Qstr_malloc_buffer = true;
    }

  qstr_Buf_idx = 0;
}

static void
loader_append_string (char c)
{
  if (use_Qstr_malloc_buffer == true)
    {
      if (qstr_Buf_idx >= qstr_Malloc_buffer_size)
	{
	  qstr_Malloc_buffer_size *= 2;
	  qstr_Malloc_buffer = realloc (qstr_Malloc_buffer, qstr_Malloc_buffer_size);

	  if (qstr_Malloc_buffer == NULL)
	    {
	      er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_OUT_OF_VIRTUAL_MEMORY, 1, (size_t) qstr_Malloc_buffer_size);
	      YY_FATAL_ERROR (er_msg ());
	      return;
	    }

	  qstr_Buf_p = qstr_Malloc_buffer;
	}
    }
  else
    {
      if (qstr_Buf_idx >= MAX_QUOTED_STR_BUF_SIZE)
	{
	  if (qstr_Malloc_buffer != NULL && qstr_Malloc_buffer_size <= MAX_QUOTED_STR_BUF_SIZE)
	    {
	      free_and_init (qstr_Malloc_buffer);
	      qstr_Malloc_buffer = NULL;
	    }

	  if (qstr_Malloc_buffer == NULL)
	    {
	      qstr_Malloc_buffer_size = MAX_QUOTED_STR_BUF_SIZE * 2;
	      qstr_Malloc_buffer = malloc (qstr_Malloc_buffer_size);

	      if (qstr_Malloc_buffer == NULL)
		{
		  er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE,
			  ER_OUT_OF_VIRTUAL_MEMORY, 1, (size_t) qstr_Malloc_buffer_size);
		  YY_FATAL_ERROR (er_msg ());
		  return;
		}
	    }

	  memcpy (qstr_Malloc_buffer, qstr_Buf_p, qstr_Buf_idx);
	  qstr_Buf_p = qstr_Malloc_buffer;
	  qstr_Buf_pool_idx--;
	  use_Qstr_malloc_buffer = true;
	}
    }

  qstr_Buf_p[qstr_Buf_idx] = c;
  qstr_Buf_idx++;
}

static LDR_STRING *
loader_get_string_container (void)
{
  LDR_STRING *str;

  if (string_Pool_idx < STRING_POOL_SIZE)
    {
      str = &(string_Pool[string_Pool_idx]);
      str->need_free_self = false;
      string_Pool_idx++;
    }
  else
    {
      str = (LDR_STRING *) malloc (sizeof (LDR_STRING));

      if (str == NULL)
	{
	  er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_OUT_OF_VIRTUAL_MEMORY, 1, sizeof (LDR_STRING));
	  YY_FATAL_ERROR (er_msg ());
	  return NULL;
	}

      str->need_free_self = true;
    }

  return str;
}

static LDR_STRING *
loader_make_string_by_yytext (void)
{
  LDR_STRING *str;
  char *invalid_pos = NULL;

  str = loader_get_string_container ();
  if (str == NULL)
    {
      return NULL;
    }

  str->size = loader_yyleng;

  if (copy_Buf_pool_idx < COPY_BUF_POOL_SIZE && str->size < MAX_COPY_BUF_SIZE)
    {
      str->val = &(copy_Buf_pool[copy_Buf_pool_idx][0]);
      memcpy (str->val, loader_yytext, str->size);
      str->val[str->size] = '\0';
      str->need_free_val = false;
      copy_Buf_pool_idx++;
    }
  else
    {
      str->val = malloc (str->size + 1);

      if (str->val == NULL)
	{
	  er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_OUT_OF_VIRTUAL_MEMORY, 1, (size_t) (str->size + 1));
	  YY_FATAL_ERROR (er_msg ());
	  return NULL;
	}

      memcpy (str->val, loader_yytext, str->size);
      str->val[str->size] = '\0';
      str->need_free_val = true;
    }

  if (intl_check_string (str->val, str->size, &invalid_pos, LANG_SYS_CODESET) != 0)
    {
      er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_INVALID_CHAR, 1, invalid_pos - str->val);
      YY_FATAL_ERROR (er_msg ());
      return NULL;
    }

  return str;
}

static LDR_STRING *
loader_make_string_by_buffer (void)
{
  LDR_STRING *str;
  char *invalid_pos = NULL;

  str = loader_get_string_container ();
  if (str == NULL)
    {
      return NULL;
    }

  str->size = qstr_Buf_idx - 1;

  if (use_Qstr_malloc_buffer == false)
    {
      str->val = qstr_Buf_p;
      str->need_free_val = false;
    }
  else
    {
      if (copy_Buf_pool_idx < COPY_BUF_POOL_SIZE && str->size < MAX_COPY_BUF_SIZE)
	{
	  str->val = &(copy_Buf_pool[copy_Buf_pool_idx][0]);
	  memcpy (str->val, qstr_Buf_p, qstr_Buf_idx);
	  str->need_free_val = false;
	  copy_Buf_pool_idx++;
	}
      else
	{
	  str->val = (char *) malloc (qstr_Buf_idx);

	  if (str->val == NULL)
	    {
	      if (str->need_free_self)
		{
		  free_and_init (str);
		}

	      er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_OUT_OF_VIRTUAL_MEMORY, 1, (size_t) qstr_Buf_idx);
	      YY_FATAL_ERROR (er_msg ());
	      return NULL;
	    }

	  memcpy (str->val, qstr_Buf_p, qstr_Buf_idx);
	  str->need_free_val = true;
	}
    }

  if (intl_check_string (str->val, str->size, &invalid_pos, LANG_SYS_CODESET) != 0)
    {
      er_set (ER_ERROR_SEVERITY, ARG_FILE_LINE, ER_INVALID_CHAR, 1, invalid_pos - str->val);
      YY_FATAL_ERROR (er_msg ());
      return NULL;
    }

  return str;
}

void
loader_reset_string_pool (void)
{
  string_Pool_idx = 0;
  copy_Buf_pool_idx = 0;
  qstr_Buf_pool_idx = 0;
}

void
loader_initialize_lexer (void)
{
  string_Pool_idx = 0;
  copy_Buf_pool_idx = 0;
  qstr_Buf_pool_idx = 0;
  qstr_Malloc_buffer = NULL;
}

void
loader_load_fail ()
{
  ldr_load_failed_error ();
  ldr_increment_fails ();
}
